"use strict";

var _regex = require("../regex");

describe('utils/regex.js', function () {
  it('should get linear gradient valuees', function () {
    expect('linear-gradient(#ccc)'.match(_regex.linearGradientRe)[0]).toEqual('#ccc');
    expect('linear-gradient(#ccc, #fff)'.match(_regex.linearGradientRe)[1]).toEqual('#fff');
    expect('linear-gradient(#ccc, #fff, rgb(254, 211, 200))'.match(_regex.linearGradientRe)[2]).toEqual('rgb(254, 211, 200)');
  });
});